package javaClass;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int a=7;
     int b=3;
     float c=a/b;
     float d=5;
     float div=a/d;
     float e=911;
     float div1=e/a;
     System.out.println("div is:"+c);
     System.out.println("div is:"+5/2);
     System.out.println("div is:"+div);
     System.out.println("div is:"+div1);
     //bitwise operators
     int x=5&7;
     System.out.println("sol is:"+x);
     int y=5|9;
     System.out.println("div is:"+y);
     int z=5^9;
     System.out.println("div is:"+z);
     int l=~5;
     System.out.println("div is:"+l);
     //shift operators
     int m=8>>1;
     System.out.println("sol is:"+m);
     int n=-16>>1;
     System.out.println("sol is:"+n);
     int o=-16>>>1;
     System.out.println("sol is:"+o);
     int p=8<<1;
     System.out.println("sol is:"+p);
     int q=8<<2;
     System.out.println("sol is:"+q);
     
     
	}

}
